import './App.css';
import React, {useState} from 'react';
import axios from 'axios';


function App() {
  const [name, setName] = useState("");
  const [gotit, setGotit] = useState("not");

  async function postname (e) {
    e.preventDefault()

    try {
      const {istheone} = await axios.post("http://localhost:4000/post_name", {name});
      setGotit(istheone);
      // if (istheone === "true") {
      //   setGotit("");
      // }
      // if (istheone === "false") {
      //   setGotit("lol");
      // }
      // else{
      //   setGotit("buns");
      // }
    }
    catch (error) {
      console.log("error")
    }
  }

  return (
    <div className="App">
      <h1>Guess the magic word!</h1>
      <form onSubmit = {postname}>
        <input type="text" value = {name} onChange = {(e) => setName(e.target.value)} />
        <button type="submit">SUBMIT YOUR NAME</button>
        <p>your name is {name}</p>
        <p>you have {gotit} got it</p>
      </form>
    </div>
  );
}

export default App;
