import './App.css';
import React, {useState} from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState("");
  const [gotit, setGotit] = useState("not");

  async function postname (e) {
    e.preventDefault()

    try {
      await axios.post("http://localhost:4000/post_name", {name});
    }
    catch (error) {
      console.log("error")
    }
  }

  function buttonHandler (e) {
    setName(e.target.value);
    if(e.target.value == "asdfasdf") {
      setGotit("");
    }
  }

  return (
    <div className="App">
      <h1>Guess the magic word!</h1>
      <form onSubmit = {postname}>
        <input type="text" value = {name} onChange = {(buttonHandler)} />
        <button type="submit">SUBMIT YOUR NAME</button>
        <p>your name is {name}</p>
        <p>you have {gotit} got it</p>
      </form>
    </div>
  );
}

export default App;
