import './App.css';
import React, {useState} from 'react';
import axios from 'axios';


function App() {
  const [name, setName] = useState("");
  const [gotit, setGotit] = useState("not");

  async function postname (e) {
    e.preventDefault()

    try {
      axios.post("http://localhost:4000/post_name", {name})
